{% capture tags %}
  {% for tag in site.tags %}
    {{ tag[0] }}
  {% endfor %}
{% endcapture %}
{% assign sortedtags = tags | split:' ' | sort %}

{% for tag in sortedtags %}
  <h3 id="{{ tag | url_encode}}">{{ tag }}</h3>
  <ul>
  {% for post in site.tags[tag] %}
    <li>{{ post.date |date:"%B %d, %Y"}} <a href="{{ post.url }}">{{ post.title }}</a></li>
  {% endfor %}
  </ul>
{% endfor %}
